import React, { useState } from 'react';
import Web3 from 'web3';
import smartWalletABI from './abi/SmartWallet.json';

const smartWalletAddress = "0x2F150c5eE87392Acc4DBDfC3633cee19c3C8bD55";

const SendFunds = () => {
    const [recipient, setRecipient] = useState('');
    const [amount, setAmount] = useState('');
    const [web3, setWeb3] = useState(null);
    const [accounts, setAccounts] = useState([]);

    const initWeb3 = async () => {
        if (window.ethereum) {
            const web3Instance = new Web3(window.ethereum);
            setWeb3(web3Instance);
            try {
                // Request account access if needed
                await window.ethereum.request({ method: 'eth_requestAccounts' });
                const accs = await web3Instance.eth.getAccounts();
                setAccounts(accs);
            } catch (error) {
                console.error('Error connecting to wallet:', error);
                alert('Failed to connect wallet. Check the console for more details.');
            }
        } else {
            alert('MetaMask is not installed. Please install MetaMask to use this DApp.');
        }
    };

    const sendEther = async () => {
        if (!web3 || !accounts.length) {
            alert("Please connect your wallet first!");
            return;
        }

        try {
            const contract = new web3.eth.Contract(smartWalletABI.abi, smartWalletAddress);
            const amountInWei = web3.utils.toWei(amount, 'ether');
            await contract.methods.sendEther(recipient, amountInWei)
                .send({ from: accounts[0] });

            alert(`Sent ${amount} ETH to ${recipient}`);
        } catch (error) {
            console.error('Error sending Ether:', error);
            alert('Failed to send Ether. Check the console for more details.');
        }
    };

    return (
        <div>
            <input 
                type="text" 
                value={recipient} 
                onChange={(e) => setRecipient(e.target.value)} 
                placeholder="Recipient Address"
            />
            <input 
                type="text" 
                value={amount} 
                onChange={(e) => setAmount(e.target.value)} 
                placeholder="Amount in Ether"
            />
            <button onClick={sendEther}>Send Ether</button>
        </div>
    );
};

export default SendFunds;
