import React, { useState } from 'react';
import Web3 from 'web3';
import SmartWalletABI from './abi/SmartWallet.json'; // Import Smart Wallet contract ABI

const smartWalletAddress = "0xa16E02E87b7454126E5E10d957A927A7F5B5d2be"; // Replace with your Smart Wallet contract's address

const WalletDisplay = () => {
    const [address, setAddress] = useState('');
    const [balance, setBalance] = useState('');

    const fetchBalance = async () => {
        try {
            // Initialize Web3 with the current provider (MetaMask)
            const web3 = new Web3(window.ethereum);
    
            // Create a contract instance for the Smart Wallet contract
            const contract = new web3.eth.Contract(SmartWalletABI.abi, smartWalletAddress);
    
            // Call the balanceOf method of the Smart Wallet contract
            const result = await contract.methods.getBalance(address).call();
    
            // Convert the result to ether (if it's in wei)
            const balanceInEther = web3.utils.fromWei(result, 'ether');
            setBalance(balanceInEther);
        } catch (error) {
            console.error('Error fetching balance:', error);
            
            // Enhanced error message
            if (error.message.includes('Failed to fetch')) {
                alert('Failed to connect to the Ethereum network. Check your provider or network connection.');
            } else {
                alert('Failed to fetch balance. Check the console for more details.');
            }
        }
    };
    
    // Define the inline CSS for your input and button
    const inputStyle = {
        padding: '10px',
        margin: '10px',
        fontSize: '16px',
    };

    const buttonStyle = {
        padding: '10px 20px',
        background: 'blue',
        color: 'white',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer',
    };

    return (
        <div>
            <input
                type="text"
                value={address}
                onChange={(e) => setAddress(e.target.value)}
                placeholder="Enter Ethereum Address"
                style={inputStyle} // Apply input style
            />
            <button onClick={fetchBalance} style={buttonStyle}>Check Balance</button> {/* Apply button style */}
            {balance && <p>Balance: {balance} ETH</p>}
        </div>
    );
};

export default WalletDisplay;
