import React, { useState } from 'react';
import Web3 from 'web3';
import smartWalletABI from './abi/SmartWallet.json'
const smartWalletAddress = "0xed7D26d6A04FC8318ab88e43eEB281ef296E509e";

const SendFunds = () => {
    const [recipient, setRecipient] = useState('');
    const [amount, setAmount] = useState('');

    const sendEther = async () => {
        try {
            // Connect to Ethereum network
            const web3 = new Web3(Web3.givenProvider || "http://localhost:8545");
            const accounts = await web3.eth.getAccounts();
            const contract = new web3.eth.Contract(smartWalletABI, smartWalletAddress);

            // Assuming the contract has a function 'sendEther' that takes recipient and amount
            const amountInWei = web3.utils.toWei(amount, 'ether');
            await contract.methods.sendEther(recipient, amountInWei)
                .send({ from: accounts[0] });

            alert(`Sent ${amount} ETH to ${recipient}`);
        } catch (error) {
            console.error('Error sending Ether:', error);
            alert('Failed to send Ether. Check the console for more details.');
        }
    };

    return (
        <div>
            <input 
                type="text" 
                value={recipient} 
                onChange={(e) => setRecipient(e.target.value)} 
                placeholder="Recipient Address"
            />
            <input 
                type="text" 
                value={amount} 
                onChange={(e) => setAmount(e.target.value)} 
                placeholder="Amount in Ether"
            />
            <button onClick={sendEther}>Send Ether</button>
        </div>
    );
};

export default SendFunds;
