import React, { useState } from 'react';
import Web3 from 'web3';
import SmartWalletABI from './abi/SmartWallet.json'; // Importing ABI

const smartWalletAddress = "0xf3495e3f6F38DDb5874944c18247dF3708880450"; // Replace with your contract's address

const DepositFunds = () => {
    const [isWalletConnected, setIsWalletConnected] = useState(false);
    const [web3Instance, setWeb3Instance] = useState(null);
    const [depositAmount, setDepositAmount] = useState('');
    
    // Check if the wallet is connected when the component mounts
    React.useEffect(() => {
        checkWalletConnection();
    }, []);

    const checkWalletConnection = async () => {
        try {
            // Check if there's an Ethereum provider (wallet) available
            if (window.ethereum) {
                await window.ethereum.request({ method: 'eth_requestAccounts' });
                const web3 = new Web3(window.ethereum);
                setWeb3Instance(web3);
                setIsWalletConnected(true);
            } else {
                setIsWalletConnected(false);
            }
        } catch (error) {
            console.error("Error connecting to wallet:", error);
            setIsWalletConnected(false);
        }
    };

    const depositFunds = async () => {
        if (!isWalletConnected) {
            alert("Please connect your wallet first!");
            return;
        }

        if (!depositAmount || isNaN(depositAmount)) {
            alert("Please enter a valid deposit amount.");
            return;
        }

        try {
            // Convert the deposit amount to wei
            const depositAmountWei = web3Instance.utils.toWei(depositAmount, 'ether');

            // Connect to your SmartWallet contract
            const contract = new web3Instance.eth.Contract(
                SmartWalletABI.abi,
                smartWalletAddress
            );

            // Example: Get the user's address
            const accounts = await web3Instance.eth.getAccounts();
            const userAddress = accounts[0];

            // Send the deposit transaction
            await contract.methods.deposit().send({
                from: userAddress,
                value: depositAmountWei,
            });

            alert(`Successfully deposited ${depositAmount} Ether.`);
        } catch (error) {
            console.error("Error depositing funds:", error);
            alert("Failed to deposit funds. Check the console for more details.");
        }
    };

    return (
        <div>
            {isWalletConnected && (
                <div>
                    <input
                        type="text"
                        value={depositAmount}
                        onChange={(e) => setDepositAmount(e.target.value)}
                        placeholder="Enter deposit amount in Ether"
                    />
                    <button onClick={depositFunds}>Deposit</button>
                </div>
            )}

            {!isWalletConnected && (
                <p>Please connect your wallet to deposit funds.</p>
            )}
        </div>
    );
};

export default DepositFunds;
