import React, { useState } from 'react';
import Web3 from 'web3';
import SmartWalletDeployerABI from './abi/SmartWalletDeployer.json'; // Importing ABI

const smartWalletDeployerAddress = "0xf3495e3f6F38DDb5874944c18247dF3708880450"; // Replace with your contract's address

const CreateWallet = () => {
    const [isWalletConnected, setIsWalletConnected] = useState(false);
    const [web3Instance, setWeb3Instance] = useState(null);

    const connectWallet = async () => {
        try {
            await window.ethereum.request({ method: 'eth_requestAccounts' });
            const web3 = new Web3(window.ethereum);
            setWeb3Instance(web3);
            setIsWalletConnected(true);
        } catch (error) {
            console.error("Error connecting to wallet:", error);
            alert("Failed to connect wallet!");
        }
    };

    const createWallet = async () => {
        if (!isWalletConnected) {
            alert("Please connect your wallet first!");
            return;
        }

        try {
            // Correctly extracting and using the ABI
            const contractABI = SmartWalletDeployerABI.abi;
            
            // Connect to your SmartWalletDeployer contract
            const contract = new web3Instance.eth.Contract(
                contractABI,
                smartWalletDeployerAddress
            );

            // Example values for contract function
            const accounts = await web3Instance.eth.getAccounts();
            const ownerAddress = accounts[0];
            const salt = Date.now(); // Example salt

            // Send transaction to create a new wallet
            await contract.methods.deploySmartWallet(ownerAddress, salt).send({ from: ownerAddress });
            alert("New wallet created!");
        } catch (error) {
            console.error("Error creating wallet:", error);
            alert("Failed to create wallet!");
        }
    };

    return (
        <div>
            {!isWalletConnected && (
                <button onClick={connectWallet}>Connect Wallet</button>
            )}
            <button onClick={createWallet} disabled={!isWalletConnected}>Create New Wallet</button>
        </div>
    );
};

export default CreateWallet;
